import styled from "styled-components";
import Announce from "../components/Announce";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import Newsletter from "../components/Newsletter";
const Container= styled.div`

`
const Wrapper= styled.div`
padding:50px;
display:flex
`
const ImgContainer= styled.div`
flex:1;
`
const InfoContainer= styled.div`
flex:1;
padding: 0px 50px;
`
const Image= styled.img`
width:100%;
height:90vh;
object-fit:cover;
`
const Title=styled.h1`
font-weight:300;
`

const Desc=styled.p`
margin:20px 0px;
`
const Price=styled.span`
font-weight:100;
font-size:40px;
`
const FilterContainer=styled.div`
width:50%; 
margin:20px;
display:flex;
justify-content:space-around;
`
const FilterTitle=styled.h4`

`
const Filter=styled.div`

display:flex;
align-items:center;
`
const FilterColor=styled.option`
width:20px;
`
const FilterSize=styled.select`
padding:5px;

`
const FilterSizeOption=styled.option`
`

const Product = () => {
    return (
        <Container>
             <Navbar/>

        <Announce/>

<Wrapper>


    <ImgContainer>
    <Image src="https://www.prada.com/content/dam/pradanux_products/U/UCS/UCS319/1YOTF010O/UCS319_1YOT_F010O_S_182_SLF.png"/>
    </ImgContainer>

    <InfoContainer>
         <Title>Prada Sweater</Title>
         <Desc>Lorem ipsum dolor sit amet consectetur adipisicing elit. Odio animi cumque impedit, veritatis voluptate assumenda id optio! Consequuntur, eveniet totam.</Desc>
         <Price>$65</Price>
         <FilterContainer>
             <Filter>
<FilterTitle>Color</FilterTitle>
<FilterColor color="black" /> 
<FilterColor color="darkblue" /> 
<FilterColor color="gray" /> 

             </Filter>

             <Filter>

<FilterTitle>Size</FilterTitle>
<FilterSize>
    <FilterSizeOption>S</FilterSizeOption>
    <FilterSizeOption>M</FilterSizeOption>
    <FilterSizeOption>L</FilterSizeOption>
    <FilterSizeOption>XL</FilterSizeOption>
    <FilterSizeOption>XXL</FilterSizeOption>
    </FilterSize>

             </Filter>

         </FilterContainer>
    </InfoContainer>
    </Wrapper>



        <Newsletter/>
        <Footer/>
         </Container>
    )
}

export default Product
