import React from "react";
import Announce from "../components/Announce";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import styled from "styled-components";
import { Add, Remove } from "@mui/icons-material";


const Container = styled.div``;
const Wrapper = styled.div`
  padding: 20px;
`;
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;
const Top = styled.div`
  display: flex;
  text-align: center;
  justify-content: space-between;
  padding: 20px;
`;
const TopButton = styled.button`
  font-weight: 600;
  padding: 10px;
  border: ${(props) => props.type === "field" && "none"};
  background-color: ${(props) =>
    props.type === "field" ? "black" : "transparent"};
  color: ${(props) => props.type === "field" && "white"};
`;
const TopTexts = styled.div``;
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

const Bottom = styled.div`
  display: flex;

  justify-content: space-between;
`;
const Info = styled.div`
  flex: 3;
`;

const Summary = styled.div`
  flex: 1;
`;
const Product = styled.div`
  display: flex;
  justify-content: space-between;
`;
const ProductDetail = styled.div`
  flex: 2;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Image = styled.img`
  width: 200px;
`;
const Details = styled.span`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const ProductName = styled.span``;
const ProductId = styled.span``;
const ProductSize = styled.span``;
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;


const ProductAmountContainer=styled.div`
display:flex;
align-items:center;

`

const ProductPrice=styled.span`
font-size:28px;
font-weight:200;
`
const ProductAmount=styled.span`
font-size:24px;
margin:5px;
`
const Hr=styled.hr`
background-color:#eee;
border:none;

height:1px;
`

const Cart = () => {
  return (
    <div>
      <Container>
        <Navbar />
        <Announce />
        <Wrapper>
          <Title>YOUR CART</Title>
          <Top>
            <TopButton>CONTINUE SHOPPING</TopButton>
            <TopTexts>
              <TopText>Shopping List</TopText>
              <TopText>Your Wishlist</TopText>
            </TopTexts>
            <TopButton type="field">CHECKOUT NOW</TopButton>
          </Top>
          <Bottom>
            <Info>
              <Product>
                <ProductDetail>
                  <Image src="https://www.prada.com/content/dam/pradanux_products/U/UCS/UCS319/1YOTF010O/UCS319_1YOT_F010O_S_182_SLF.png" />
                  <Details>
                    <ProductName>
                      <b>Product: </b> PRADA SHIRT
                    </ProductName>
                    <ProductId>
                      <b>ID: </b> 23552342342
                    </ProductId>
                    <ProductColor color="#fa8102" />
                    <ProductSize>
                      <b>Size: </b> S
                    </ProductSize>
                  </Details>
                </ProductDetail>
                <PriceDetail>
                  <ProductAmountContainer>
                  <Add/>
                  <ProductAmount> 2</ProductAmount>
                  <Remove />
                  </ProductAmountContainer >
                  <ProductPrice>$ 45</ProductPrice>
                </PriceDetail>
              </Product>
<Hr/>
              <Product>
                <ProductDetail>
                  <Image src="https://d3o2e4jr3mxnm3.cloudfront.net/Mens-Jake-Guitar-Vintage-Crusher-Tee_68382_1_lg.png " />
                  <Details>
                    <ProductName>
                      <b>Product: </b> DEAN SHIRT
                    </ProductName>
                    <ProductId>
                      <b>ID: </b> 44552342342
                    </ProductId>
                    <ProductColor color="#fff5ee" />
                    <ProductSize>
                      <b>Size: </b> M
                    </ProductSize>
                  </Details>
                </ProductDetail>
                <PriceDetail>
                  <ProductAmountContainer>
                  <Add/>
                  <ProductAmount> 2</ProductAmount>
                  <Remove />
                  </ProductAmountContainer >
                  <ProductPrice>$ 45</ProductPrice>
                </PriceDetail>
              </Product>
            </Info>
            <Summary>
            <SummaryTitle>
                ORDER SUMMARY
            </SummaryTitle>

            </Summary>

          </Bottom>
        </Wrapper>
        Cart
        <Footer />
      </Container>
    </div>
  );
};

export default Cart;
