import {
  ArrowBackIosRounded,
  ArrowForwardIosRounded,
} from "@mui/icons-material";
import styled from "styled-components";
const Container = styled.div`

  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
`;
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  top: 0;
  bottom: 0;
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`;
const Wrapper = styled.div`
  height: 100%;
`;
const Slide = styled.div`
  display: flex;
  align-items: center;
  width: 100vw;

  height: 100vh;
  justify-content:center;
`;
const ImgContainer = styled.div`
  flex: 1;
`;
const Image = styled.img`
  width:100%;

`;
const InfoContainer = styled.div`
  padding:50px;
`;

const Title=styled.h1`

`
const Description=styled.p`

`
const Button=styled.button`

`
const Slider = () => {
  return (
    <div>
      <Container>
        <Arrow direction="left">
          <ArrowBackIosRounded />
        </Arrow>
        <Wrapper>
          <Slide>
            <ImgContainer>
              <Image src="https://img-lcwaikiki1.mncdn.com/mnresize/1920/-/Resource/Images/Banner/201221-hediye.jpg" />
            </ImgContainer>
            <InfoContainer>
<Title>SUMMER SAL</Title>
<Description>DON'T MİSS THİS DEAL! IT WILL EN FRIDAY</Description>
<Button>SHOW NOW</Button>

            </InfoContainer>
          </Slide>
        </Wrapper>
        <Arrow direction="right">
          <ArrowForwardIosRounded />
        </Arrow>
      </Container>
    </div>
  );
};

export default Slider;
