display:flex;

justify-content:space-between;
`
const Info=styled.div`
flex:3;

`

const Summary=styled.div`
flex:1;

`

const Cart = () => {
  return (
    <div>
      <Container>
        <Navbar /> 
        <Announce />
        <Wrapper>
          <Title>YOUR CART</Title>
