    </ProductName>
</Details>

    </ProductDetail>
    <PriceDetail>

    </PriceDetail>
</Product>


              </Info>
              <Summary></Summary>
          </Bottom>
        </Wrapper>
        Cart
        <Footer />
      </Container>
    </div>
  );
};

export default Cart;
